mapper.insert:

  SELECT LAST_INSERT_ID()

insert into orderDetail (orderId, storeId, productId, quantity, department, office
  )
values (#{orderid,jdbcType=VARCHAR}, #{storeid,jdbcType=VARCHAR}, #{productid,jdbcType=VARCHAR}, #{quantity,jdbcType=VARCHAR}, #{department,jdbcType=VARCHAR}, #{office,jdbcType=VARCHAR}
  )

mapper.select:
	select t.goodsDate, t.productId, t.productName, t.productIcon, sum(t.quantity) as quantity
	from (
	    select a.quantity, a.productId, substring(c.goodsDate,6) as goodsDate, c.productName, c.productIcon
	    from orderDetail a
	    left join storeList b on a.storeId=b.storeId and b.storeStatus='1'
	    left join goodsList c on a.productId=c.productId and c.productStatus='1'
	    left join orderList d on a.orderId=d.orderId and d.orderStatus='1'
	    where 1=1

        	and a.department = #{arg0,jdbcType=CHAR}


        	and a.office = #{arg1,jdbcType=CHAR}


        	and substring(c.goodsDate,6) = #{arg2,jdbcType=CHAR}

	) t
	group by t.goodsDate, t.productId
	order by t.goodsDate, t.productId
