mapper.select:
	select orderid, DATE_FORMAT(orderdate,"%Y-%m-%d %T") as orderDate, orderQuantity, orderAmount, remarks
    from orderList
    where openid = #{arg0,jdbcType=CHAR}
    and orderstatus = #{arg1,jdbcType=CHAR}
    order by orderDate desc

mapper.update:
update orderList
set orderStatus = '2'
where orderId = #{orderid,jdbcType=VARCHAR}
